use strict;
use warnings;
use List::Util qw(sum product);

require "sub.pl";

# problem 6

my $sum_of_natural_num;
my $sum_of_squares;

foreach (1 .. 100) {
	$sum_of_natural_num+=$_;
	$sum_of_squares += $_ **2;	
}

my $diff= $sum_of_natural_num **2  - $sum_of_squares;

print "$diff\n";


# problem 7

# how about use the violent way

my $n;
my $num;

while (1) {
    $n++;
    if ( is_prime($n) ) {
        $num++;
        last if $num == 10001;
    }

}

print "$n\n";


# problem 8

my $str="";

# print length($str) . "\n";
my $max=0;

my @arr = split //,$str; 
for my $i( 0.. ($#arr-12)){
	
    my $product = product @arr[$i .. $i+12];		
    $max = $product if $max < $product;
}

print "$max\n";



# problem 9
#
# actually the free variable are only one
# because there are the two express equals here
#

for my $a(1 .. 1000){
	for my $b($a+1 .. 1000){
		my $c = 1000 - $a - $b;

		if (($c > $b) && ($a*$a + $b*$b == $c*$c)){
			print "$a\t$b\t$c\n";
			print $a*$b*$c."\n";
		}

	}
}


